import tech from "./assets/images/tech-1.jpg";
import mobile from "./assets/images/mobile-1.jpg";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

export default function Home() {
  // const [search, setsearch] = useState("");
  const [categories, setcategories] = useState([]);
  const [posts, setposts] = useState([]);

  useEffect(() => {
    getCategories();
    getPosts();
  }, []);

  const getCategories = () => {
    axios
      .post("/showCategory")
      .then((response) => {
        const res = response.data;
        const resCategories = res;
        setcategories(resCategories);
        // categories = resCategories;
        // console.log(resCategories);
      })
      .catch((err) => {
        const error = err.response.data;
      });
    console.log(categories);
  };

  const getPosts = () => {
    axios
      .post("/showPost")
      .then((response) => {
        const res = response.data;
        const resPosts = res;
        setposts(resPosts);
        // console.log(resPosts);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  };

  return (
    <div>
      <div className="header-content">
        <img
          src={tech}
          className="img-fluid"
          alt=""
          width="100%"
          height="100%"
        />
        <div className="overlay row h-100 w-100">
          <div className="w-100 p-5 my-auto text-center text-white">
            <h1 className="display-4 fw-bold">Creative Blog</h1>
            <p className="lead">
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Obcaecati
              earum consequuntur, voluptatibus officia beatae, corrupti
              voluptate cumque quisquam asperiores voluptas ex! Quisquam,
              veritatis ipsa? Dicta eligendi id hic aperiam similique.
            </p>
          </div>
        </div>
      </div>

      <div className="container py-5 text-center">
        <h2 className="display-4 pb-4">Categories</h2>
        {categories &&
          categories.map((value) => {
            return (
              <button type="button" className="btn btn-orange rounded-pill">
                {value.categoryName}
              </button>
            );
          })}
      </div>

      <div className="container py-4">
        <div className="row">
          {posts &&
            posts.map((value) => {
              return (
                <div className="col-4">
                  <Link to={"/"} className="card">
                    {/* <img className="card-img-top" src={mobile} alt="" /> */}
                    <div className="card-body">
                      <h4 className="card-title">{value.title}</h4>
                      <p className="card-text">{value.content}</p>
                      <p className="card-text">
                        {/* <small className="text-muted">Last updated 3 mins ago</small> */}
                      </p>
                    </div>
                  </Link>
                </div>
              );
            })}
          <div className="col-4">
            <Link to={"/"} className="card">
              <img className="card-img-top" src={mobile} alt="" />
              <div className="card-body">
                <h4 className="card-title">Card title</h4>
                <p className="card-text">
                  This is a wider card with supporting text below as a natural
                  lead-in to additional content. This content is a little bit
                  longer.
                </p>
                <p className="card-text">
                  <small className="text-muted">Last updated 3 mins ago</small>
                </p>
              </div>
            </Link>
          </div>
          <div className="col-4">
            <Link to={"/"} className="card">
              <img className="card-img-top" src={mobile} alt="" />
              <div className="card-body">
                <h4 className="card-title">Card title</h4>
                <p className="card-text">
                  This is a wider card with supporting text below as a natural
                  lead-in to additional content. This content is a little bit
                  longer.
                </p>
                <p className="card-text">
                  <small className="text-muted">Last updated 3 mins ago</small>
                </p>
              </div>
            </Link>
          </div>
          <div className="col-4">
            <Link to={"/"} className="card">
              <img className="card-img-top" src={mobile} alt="" />
              <div className="card-body">
                <h4 className="card-title">Card title</h4>
                <p className="card-text">
                  This is a wider card with supporting text below as a natural
                  lead-in to additional content. This content is a little bit
                  longer.
                </p>
                <p className="card-text">
                  <small className="text-muted">Last updated 3 mins ago</small>
                </p>
              </div>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}
