import * as bootstrap from "bootstrap";
import axios from "axios";
import { toast } from "react-toastify";
import { useState, useEffect } from "react";
import draftToHtml from "draftjs-to-html";
import MyEditor from "./editor";
import Modal from "../../components/modal";
import SelectCategories from "../../components/selectCategories";
import SelectTags from "../../components/selectTags";

export default function EditPost() {
  const [content, setContent] = useState(null);
  const [editorContent, seteditorContent] = useState(null);
  const [editor, seteditor] = useState(false);
  const [formInput, setformInput] = useState(null);

  useEffect(() => {
    showPosts();
  }, []);

  function setModal(post_card) {
    const postID = post_card.getAttribute("post_id");
    const postTitle = post_card.querySelector(
      ".card-body > .card-title"
    ).textContent;
    const postContent = post_card.querySelector(
      ".card-body > .card-text"
    ).innerHTML;

    seteditorContent(postContent);
    seteditor(true);
    // console.log(editorContent);

    // console.log(postID);
    // console.log(postTitle);

    var myModalEl = document.getElementById("editPostModal");
    var myModal = bootstrap.Modal.getOrCreateInstance(myModalEl);
    myModal.show();

    myModalEl.addEventListener("shown.bs.modal", () => {
      document.getElementById("postTitle").value = postTitle;
      setformInput({
        ...formInput,
        postID,
        postTitle,
      });
      // console.log(formInput);
    });

    myModalEl.addEventListener("hidden.bs.modal", () => {
      seteditor(false);
      console.log("closed");
      // console.log(formInput);
    });
  }

  function showPosts() {
    axios
      .post("/showPost")
      .then((response) => {
        const res = response.data;
        let postData = "";

        res.forEach((value) => {
          // console.log(value);

          postData += `<div class="col-4 mb-2"><div class="card shadow-sm modal-click" post_id="${
            value._id
          }"><div class="card-body"><h4 class="card-title">${
            value.title
          }</h4><div class="card-text">${draftToHtml(
            value.content
          )}</div></div></div></div>`;
        });
        // console.log(postData);
        document.getElementById("postContainer").innerHTML = postData;

        const post_cards = [].slice.call(
          document.getElementsByClassName("modal-click")
        );
        post_cards.forEach((post_card) => {
          post_card.addEventListener("click", () => {
            setModal(post_card);
          });
        });
        // return data;
        // console.log(res);
      })
      .catch((err) => {
        const error = err;
        console.log(error);
      });
  }

  function handleInput(e) {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    setformInput({
      ...formInput,
      [name]: value,
    });
  }

  async function handleSubmit(e) {
    e.preventDefault();
    const postContent = content;
    // setformInput({ ...formInput, postContent: "editorcontent" });

    axios
      .post("/editPost", { ...formInput, postContent })
      .then((response) => {
        const res = response.data;
        console.log(res.success);
      })
      .catch((err) => {
        const error = err.response.data;
        console.log(error);
      });

    // console.log(formInput);
  }

  function handleDelete(e) {
    e.preventDefault();

    const postID = formInput.postID;

    // console.log();

    axios
      .post("/deletePost", { postID })
      .then((response) => {
        const res = response.data;
        const success = res.success;

        console.log(success);
        if (success.length > 0) {
          showPosts();
        }
      })
      .catch((err) => {
        const error = err.response.data;
      });

    var myModal = bootstrap.Modal.getOrCreateInstance(
      document.getElementById("editPostModal")
    );
    myModal.hide();
  }

  const getValue = (value) => {
    // check do you get dynamic value here
    // console.log("EditorValue: " + JSON.stringify(value));
    setContent(value);
  };

  const getCategories = (value) => {
    setformInput({ ...formInput, postCategories: value });
    console.log("Category: ", value);
  };
  const getTags = (value) => {
    setformInput({ ...formInput, postTags: value });
    console.log("Tags: ", value);
  };

  return (
    <div className="px-5">
      {/* <MyEditor2 getValue={getValue} /> */}
      <div className="card shadow">
        <div className="card-body">
          <div id="postContainer" className="row"></div>
          <Modal id={"editPostModal"} title={"Edit Post"}>
            <form>
              <div className="mb-3">
                <label
                  htmlFor="postTitle"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Title
                </label>
                <div className="col-12">
                  <input
                    type="text"
                    className="form-control rounded-pill"
                    name="postTitle"
                    id="postTitle"
                    onChange={handleInput}
                  />
                </div>
              </div>

              <div className="mb-3">
                <label
                  htmlFor="post_cat"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Categories
                </label>
                <SelectCategories getCategories={getCategories} />
              </div>
              <div className="mb-3">
                <label
                  htmlFor="post_cat"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Tags
                </label>
                <SelectTags getTags={getTags} />
              </div>
              <div className="mb-3">
                <label
                  htmlFor="postDes"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Content
                </label>
                <div className="col-12">
                  {editor && (
                    <MyEditor
                      getValue={getValue}
                      editorContent={editorContent}
                    />
                  )}
                </div>
              </div>
              <div className="mb-5">
                <div className="col-sm-10">
                  <button
                    type="submit"
                    className="btn btn-orange"
                    onClick={handleSubmit}
                  >
                    Edit Post
                  </button>
                  <button
                    type="submit"
                    className="btn btn-danger ms-2"
                    // onClick={handleDelete}
                  >
                    Delete Post
                  </button>
                </div>
              </div>
            </form>
          </Modal>
        </div>
      </div>
    </div>
  );
}
