import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import draftToHtml from "draftjs-to-html";

export default function Post() {
  const params = useParams();

  const [title, settitle] = useState("");
  const [content, setcontent] = useState("");

  useEffect(() => {
    getPost(params.id);
  });

  function getPost(id) {
    axios
      .post("/getPost", { postID: id })
      .then((response) => {
        const res = response.data;

        const postTitle = res.title;
        const postContent = res.content;

        console.log(draftToHtml(postContent));

        const convertedContent = draftToHtml(postContent);

        settitle(postTitle);
        setcontent(convertedContent);
        // console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  return (
    <div className="container">
      <div className="row">
        <div className="col-8 py-5">
          <h1>{title && title}</h1>
          <hr />
          <img
            src="/images/mobile-1.jpg"
            className="img-fluid rounded p-0 pb-5"
            alt=""
          />
          {content && content}
        </div>
        <div className="col-4"></div>
      </div>
    </div>
  );
}
