import axios from "axios";
import { useState, useEffect } from "react";
import draftToHtml from "draftjs-to-html";

import { Link } from "react-router-dom";

export default function LatestPosts() {
  const [searchPosts, setsearchPosts] = useState("");

  const [posts, setposts] = useState([]);
  const [allPosts, setallPosts] = useState([]);

  useEffect(() => {
    getAllPosts();
  }, []);

  function searchForPosts(e) {
    e.preventDefault();

    const search = searchPosts;

    axios
      .post("/searchPost", { search })
      .then((response) => {
        const res = response.data;
        setallPosts([]);
        setposts(res);
        console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  function getAllPosts() {
    axios
      .post("/showPost")
      .then((response) => {
        const res = response.data;
        setallPosts(res);
        console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  return (
    <div className="container py-5">
      <div className="text-center pb-5">
        <h2 className="display-5">Posts</h2>
      </div>
      <div className="row">
        <div className="col-10">
          {/* <label for="" className="form-label"></label> */}
          <input
            type="text"
            name="search"
            id="search"
            className="form-control rounded-pill"
            placeholder="Search"
            aria-describedby="helpId"
            onChange={(e) => setsearchPosts(e.target.value)}
          />
        </div>
        <button
          className="btn btn-orange rounded-pill col-2"
          onClick={searchForPosts}
        >
          Search
        </button>

        <div className="container py-4">
          <hr />
          <div className="row">
            {posts &&
              posts.map((value) => {
                return (
                  <div className="col-4 mb-3">
                    <Link to={"/post/" + value._id} className="card">
                      {/* <img className="card-img-top" src={mobile} alt="" /> */}
                      <div className="card-body">
                        <h4 className="card-title">{value.title}</h4>
                        <p
                          className="card-text"
                          dangerouslySetInnerHTML={{
                            __html: draftToHtml(value.content),
                          }}
                        ></p>
                        <p className="card-text">
                          {/* <small className="text-muted">Last updated 3 mins ago</small> */}
                        </p>
                      </div>
                    </Link>
                  </div>
                );
              })}
            {allPosts &&
              allPosts.map((value) => {
                return (
                  <div className="col-4 mb-3">
                    <Link to={"/post/" + value._id} className="card">
                      {/* <img className="card-img-top" src={mobile} alt="" /> */}
                      <div className="card-body">
                        <h4 className="card-title">{value.title}</h4>
                        <p
                          className="card-text"
                          dangerouslySetInnerHTML={{
                            __html: draftToHtml(value.content),
                          }}
                        ></p>
                        <p className="card-text">
                          {/* <small className="text-muted">Last updated 3 mins ago</small> */}
                        </p>
                      </div>
                    </Link>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    </div>
  );
}
