import React, { useState, useEffect, useRef, useMemo } from "react";
import JoditEditorsss from "jodit-react";

const Editor = ({ getValue, content }) => {
  const editor = useRef(null);

  // useEffect(() => {}, [content]);

  const config = {
    buttons: [
      "bold",
      "italic",
      "underline",
      "strikethrough",
      "eraser",
      "ul",
      "ol",
      "font",
      "fontsize",
      "paragraph",
      "classSpan",
      "lineHeight",
      "image",
      "video",
      "file",
      "spellcheck",
      "copyformat",
      "cut",
      "copy",
    ],
    readonly: false, // all options from https://xdsoft.net/jodit/doc/
  };

  return (
    <JoditEditor
      ref={editor}
      config={config}
      value={content}
      tabIndex={1} // tabIndex of textarea
      onBlur={(newContent) => getValue(newContent)} // preferred to use only this option to update the content for performance reasons
      onChange={(newContent) => {
        getValue(newContent);
      }}
    />
  );
};

export default Editor;
