import * as bootstrap from "bootstrap";
import axios from "axios";
import { toast } from "react-toastify";
import { useState, useEffect } from "react";
// import Editor from "./editorDemo";
import Modal from "../../components/modal";

export default function EditPost() {
  // const [content, setContent] = useState("data");
  // const [editor, seteditor] = useState(false);
  const [formInput, setformInput] = useState(null);

  // this.state = {

  // }

  useEffect(() => {
    showPosts();
  }, []);

  // useEffect(() => {
  //   setformInput({ postContent: "" });
  //   console.log(formInput);
  // }, [content]);

  function setModal(post_card) {
    const postID = post_card.getAttribute("post_id");
    const postTitle = post_card.querySelector(
      ".card-body > .card-title"
    ).textContent;
    const postContent = post_card.querySelector(
      ".card-body > .card-text"
    ).textContent;

    let editorContent = "";
    // if (postID && postTitle && postContent) {
    // console.log(postID);
    // }
    // setContent(postContent);
    // editorContent = content;
    // setformInput({ ...formInput, postID, postTitle });
    // seteditor(true);

    // console.log(formInput);

    var myModalEl = document.getElementById("editPostModal");
    var myModal = bootstrap.Modal.getOrCreateInstance(myModalEl);
    myModal.show();

    myModalEl.addEventListener("shown.bs.modal", () => {
      document.getElementById("postTitle").value = postTitle;
      setformInput({ ...formInput, postID, postTitle });
      console.log(formInput);
      // setContent(postContent);
      // document.querySelector(".jodit-wysiwyg").innerHTML = postContent;
    });

    myModalEl.addEventListener("hidden.bs.modal", () => {
      console.log("closed");
      // document.getElementById("postTitle").value = postTitle;
      // setContent(postContent);
      console.log(formInput);
      // seteditor(false);
      // document.querySelector(".jodit-wysiwyg").innerHTML = postContent;
    });
  }

  function showPosts() {
    axios
      .post("/showPost")
      .then((response) => {
        const res = response.data;
        let postData = "";

        res.forEach((value) => {
          // console.log(value);
          postData += `<div class="col-4 mb-2"><div class="card shadow-sm modal-click" post_id="${value._id}"><div class="card-body"><h4 class="card-title">${value.title}</h4><p class="card-text">${value.content}</p></div></div></div>`;
        });
        // console.log(postData);
        document.getElementById("postContainer").innerHTML = postData;

        const post_cards = [].slice.call(
          document.getElementsByClassName("modal-click")
        );
        post_cards.forEach((post_card) => {
          post_card.addEventListener("click", () => {
            // console.log("modal open");
            // setModal(post_card);
            const postID = post_card.getAttribute("post_id");
            const postTitle = post_card.querySelector(
              ".card-body > .card-title"
            ).textContent;
            const postContent = post_card.querySelector(
              ".card-body > .card-text"
            ).textContent;

            console.log(postID);
            console.log(postTitle);
            console.log(postContent);

            var myModalEl = document.getElementById("editPostModal");
            var myModal = bootstrap.Modal.getOrCreateInstance(myModalEl);
            myModal.show();

            myModalEl.addEventListener("shown.bs.modal", () => {
              document.getElementById("postTitle").value = postTitle;
              setformInput({ postID, postTitle });
              setTimeout(() => {
                console.log(formInput);
              }, 2000);
              // setContent(postContent);
            });

            myModalEl.addEventListener("hidden.bs.modal", () => {
              console.log("closed");
              // document.getElementById("postTitle").value = postTitle;
              // setContent(postContent);
              // console.log(formInput);
              // seteditor(false);
            });
          });
        });
        // return data;
        // console.log(res);
      })
      .catch((err) => {
        const error = err;
        console.log(error);
      });
  }

  function handleinput(e) {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    setformInput({
      ...formInput,
      [name]: value,
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    // const editorcontent = content;
    setformInput({ ...formInput, postContent: "editorcontent" });

    console.log(formInput);
    axios
      .post("/editPost", formInput)
      .then((response) => {
        const res = response.data;
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  function handleDelete(e) {
    e.preventDefault();

    const postID = formInput.postID;

    // console.log();

    axios
      .post("/deletePost", { postID })
      .then((response) => {
        const res = response.data;
        const success = res.success;

        console.log(success);
        if (success.length > 0) {
          showPosts();
        }
      })
      .catch((err) => {
        const error = err.response.data;
      });

    var myModal = bootstrap.Modal.getOrCreateInstance(
      document.getElementById("editPostModal")
    );
    myModal.hide();
  }

  // const getValue = (value) => {
  //   // check do you get dynamic value here
  //   // console.log(value);
  //   setContent(value);
  // };

  return (
    <div className="px-5">
      <div className="card shadow">
        <div className="card-body">
          <div id="postContainer" className="row"></div>
          <Modal id={"editPostModal"} title={"Edit Post"}>
            <form>
              <div className="mb-3">
                <label
                  htmlFor="postTitle"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Title
                </label>
                <div className="col-12">
                  <input
                    type="text"
                    className="form-control rounded-pill"
                    name="postTitle"
                    id="postTitle"
                    onChange={handleinput}
                  />
                </div>
              </div>
              <div className="mb-3">
                <label
                  htmlFor="postDes"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Content
                </label>
                <div className="col-12">
                  {/* {editor && <Editor getValue={getValue} content={content} />} */}
                  {/* <Editor getValue={getValue} content={content} /> */}
                </div>
              </div>
              <div className="mb-5">
                <div className="col-sm-10">
                  <button
                    type="submit"
                    className="btn btn-orange"
                    onClick={handleSubmit}
                  >
                    Edit Post
                  </button>
                  <button
                    type="submit"
                    className="btn btn-danger ms-2"
                    onClick={handleDelete}
                  >
                    Delete Post
                  </button>
                </div>
              </div>
            </form>
          </Modal>
        </div>
      </div>
    </div>
  );
}
