import axios from "axios";
import { toast } from "react-toastify";
import { useState } from "react";
import MyEditor from "./editor";
import ImageUpload from "../../components/imageUpload";

export default function CreatePost() {
  const [forminput, setforminput] = useState({});
  const [content, setContent] = useState(null);

  function handleInput(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    setforminput({
      ...forminput,
      [name]: value,
    });

    // console.log(forminput);
  }

  function handleSubmit(e) {
    e.preventDefault();
    // console.log(forminput.postImage);

    const config = {
      headers: {
        "content-type": "multipart/form-data",
      },
    };

    const formData = new FormData();

    Object.entries(forminput).forEach((value, index) => {
      console.log(forminput[index] + ", " + value);
    });
    // formData.append(forminput);
    formData.append("postImage", forminput.postImage);

    // for (var pair of formData.entries()) {
    //   console.log(pair[0] + ", " + pair[1]);
    // }

    axios
      .post(
        "/createPost",
        { ...forminput, formData, postContent: content },
        config
      )
      // .post("/createPost", forminput, config)
      .then((response) => {
        const res = response.data;
        const success = res.success;

        console.log(success);

        if (success.length > 0) {
          toast.success(success, {
            position: toast.POSITION.TOP_RIGHT,
          });
        }
      })
      .catch((err) => {
        const error = err.response.error;
        console.log(error);
      });
  }

  const getImage = (e) => {
    // console.log(e.target.files[0]);
    setforminput({
      postImage: e.target.files[0],
    });
  };

  const getValue = (value) => {
    // check do you get dynamic value here
    // console.log("EditorValue: " + JSON.stringify(value));
    setContent(value);
  };

  return (
    <div className="px-5">
      <div className="card shadow">
        <div className="card-body">
          <div className="h2 p-1">Create Post</div>
          <form>
            <ImageUpload label={"Upload Image"} getImage={getImage} />
            <div className="mb-3">
              <label
                htmlFor="post_title"
                className="col-12 ps-2 pb-1 text-black-50"
              >
                Title
              </label>
              <div className="col-12">
                <input
                  type="post_title"
                  className="form-control rounded-pill"
                  name="post_title"
                  id="post_title"
                  onChange={handleInput}
                />
              </div>
            </div>
            <div className="mb-3">
              <label
                htmlFor="post_des"
                className="col-12 ps-2 pb-1 text-black-50"
              >
                Content
              </label>
              <div className="col-12">
                <MyEditor getValue={getValue} editorContent={""} />
              </div>
            </div>
            <div className="mb-5">
              <div className="col-sm-10">
                <button
                  type="submit"
                  className="btn btn-orange"
                  onClick={handleSubmit}
                >
                  Submit
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
