import axios from "axios";
import { useState } from "react";

import { Link } from "react-router-dom";

export default function LatestPosts() {
  const [searchPosts, setsearchPosts] = useState("");

  const [posts, setposts] = useState([]);

  function searchForPosts(e) {
    e.preventDefault();

    const search = searchPosts;

    axios
      .post("/searchPost", { search })
      .then((response) => {
        const res = response.data;
        setposts(res);
        console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  return (
    <div class="container py-5">
      <div class="row">
        <div class="col-10 p-0">
          {/* <label for="" class="form-label"></label> */}
          <input
            type="text"
            name="search"
            id="search"
            class="form-control rounded-pill border-end-0"
            placeholder="Search"
            aria-describedby="helpId"
            onChange={(e) => setsearchPosts(e.target.value)}
          />
        </div>
        <button
          className="btn btn-orange rounded-pill col-2 p-0 border-start-0"
          onClick={searchForPosts}
        >
          Search
        </button>

        <div className="container py-4">
          <div className="row">
            {posts &&
              posts.map((value) => {
                return (
                  <div className="col-4">
                    <Link to={"/post/" + value._id} className="card">
                      {/* <img className="card-img-top" src={mobile} alt="" /> */}
                      <div className="card-body">
                        <h4 className="card-title">{value.title}</h4>
                        <p className="card-text">{value.content}</p>
                        <p className="card-text">
                          {/* <small className="text-muted">Last updated 3 mins ago</small> */}
                        </p>
                      </div>
                    </Link>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    </div>
  );
}
