import axios from "axios";
import { useState, useEffect } from "react";

import { Link } from "react-router-dom";

export default function LatestCategories() {
  const [categories, setcategories] = useState(null);
  const [searchCategories, setSearchCategories] = useState(null);

  // Search categories by name
  async function searchCategory(e, searchCategory) {
    e.preventDefault();

    const search = searchCategory;

    await axios
      .post("/searchCategory", { search })
      .then((response) => {
        const res = response.data;
        setSearchCategories(null);
        setcategories(res);
        // console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  async function getAllCategory() {
    await axios
      .post("/showCategory")
      .then((response) => {
        const res = response.data;
        setcategories(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
    console.log(categories);
  }

  useEffect(() => {
    // Get all categories on page load
    getAllCategory();
  }, []);

  return (
    <div className="container py-5">
      <div className="text-center pb-5">
        <h2 className="display-5">Categories</h2>
      </div>
      <div className="row">
        <div className="col-10">
          {/* <label for="" className="form-label"></label> */}
          <input
            type="text"
            name="search"
            id="searchCategory"
            className="form-control rounded-pill"
            placeholder="Search"
            aria-describedby="helpId"
            onChange={(e) => searchCategory(e.target.value)}
          />
        </div>
        <button
          className="btn btn-orange rounded-pill col-2"
          onClick={() =>
            searchCategory(document.getElementById("searchCategory").value)
          }
        >
          Search
        </button>

        <div className="container py-4">
          {categories &&
            categories.map((category) => {
              return (
                <Link to={"/category/" + category.categoryName}>
                  <button type="button" className="btn btn-orange rounded-pill">
                    {category.categoryName}
                  </button>
                </Link>
              );
            })}
          {/* {allCategory &&
            allCategory.map((value) => {
              return (
                <Link
                  to={"/category/" + value._id}
                  className="btn btn-orange rounded-pill me-2 mb-2"
                >
                  {value.categoryName}
                </Link>
              );
            })} */}
          {searchCategories &&
            searchCategories.map((category) => {
              return (
                <Link to={"/category/" + category.categoryName}>
                  <button type="button" className="btn btn-orange rounded-pill">
                    {category.categoryName}
                  </button>
                </Link>
              );
            })}
        </div>
      </div>
    </div>
  );
}
