import axios from "axios";
import { toast } from "react-toastify";
import { useState } from "react";
import MyEditor from "./editor";

export default function CreatePost() {
  const [forminput, setforminput] = useState({});
  const [content, setContent] = useState(null);

  function handleInput(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    setforminput({
      ...forminput,
      [name]: value,
    });

    console.log(forminput);
  }

  function handleSubmit(e) {
    e.preventDefault();
    // console.log(forminput);
    axios
      .post("/createPost", { ...forminput, postContent: content })
      .then((response) => {
        const res = response.data;
        const success = res.success;

        console.log(success);

        if (success.length > 0) {
          toast.success(success, {
            position: toast.POSITION.TOP_RIGHT,
          });
        }
      })
      .catch((err) => {
        const error = err.response.error;
        console.log(error);
      });
  }

  const getValue = (value) => {
    // check do you get dynamic value here
    // console.log("EditorValue: " + JSON.stringify(value));
    setContent(value);
  };

  return (
    <div className="px-5">
      <div className="card shadow">
        <div className="card-body">
          <div className="h2 p-1">Create Post</div>
          <form>
            <div className="mb-3">
              <label htmlFor="post_image" className="form-label text-black-50">
                Image
              </label>
              <input
                className="form-control"
                type="file"
                id="post_image"
                // onChange={(e) =>
                //   setforminput({
                //     post_image: URL.createObjectURL(e.target.files[0]),
                //   })
                // }
                onChange={(e) =>
                  setforminput({
                    post_image: URL.createObjectURL(e.target.files[0]),
                  })
                }
              />
              <img
                src={forminput.post_image && forminput.post_image}
                class="img-fluid"
                htmlFor="post_image"
                alt=""
              />
            </div>
            <div className="mb-3">
              <label
                htmlFor="post_title"
                className="col-12 ps-2 pb-1 text-black-50"
              >
                Title
              </label>
              <div className="col-12">
                <input
                  type="post_title"
                  className="form-control rounded-pill"
                  name="post_title"
                  id="post_title"
                  onChange={handleInput}
                />
              </div>
            </div>
            <div className="mb-3">
              <label
                htmlFor="post_des"
                className="col-12 ps-2 pb-1 text-black-50"
              >
                Content
              </label>
              <div className="col-12">
                <MyEditor getValue={getValue} editorContent={""} />
              </div>
            </div>
            <div className="mb-5">
              <div className="col-sm-10">
                <button
                  type="submit"
                  className="btn btn-orange"
                  onClick={handleSubmit}
                >
                  Submit
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
