import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function Search() {
  const params = useParams();

  const [searchPosts, setsearchPosts] = useState(params.search);
  const [posts, setposts] = useState([]);

  useEffect(() => {
    searchForPosts(e);
  }, []);

  function searchForPosts(e) {
    if (e) {
      e.preventDefault();
    }

    const search = searchPosts;

    axios
      .post("/searchPost", { search })
      .then((response) => {
        const res = response.data;
        setposts(res);
        console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  return (
    <div className="container">
      <div className="text-center py-5">
        <h2 className="display-5">Search</h2>
      </div>

      <div className="search-container">
        <form className="d-flex">
          <input
            className="form-control rounded-pill"
            type="text"
            placeholder="Search"
            value={params}
          />
          <button className="btn btn-outline-orange w-25" type="submit">
            Search
          </button>
        </form>
        <hr />
      </div>

      <div className="row pb-5">
        {posts &&
          posts.map((value) => {
            return (
              <div className="col-4 mb-3">
                <Link to={"/post/" + value._id} className="card">
                  {/* <img className="card-img-top" src={mobile} alt="" /> */}
                  <div className="card-body">
                    <h4 className="card-title">{value.title}</h4>
                    <p className="card-text">{value.content}</p>
                    <p className="card-text">
                      {/* <small className="text-muted">Last updated 3 mins ago</small> */}
                    </p>
                  </div>
                </Link>
              </div>
            );
          })}
      </div>
    </div>
  );
}
