import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import draftToHtml from "draftjs-to-html";

export default function Search() {
  const params = useParams();

  const [searchPosts, setsearchPosts] = useState(params.search);
  const [posts, setposts] = useState([]);

  useEffect(() => {
    searchForPosts();
  }, []);

  async function searchForPosts(e) {
    e.preventDefault();

    const search = searchPosts;

    await axios
      .post("/searchPost", { search })
      .then((response) => {
        const res = response.data;
        setposts(res);
        console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  return (
    <div className="container">
      <div className="text-center py-5">
        <h2 className="display-5">Search</h2>
      </div>

      <div className="row">
        <div className="col-10">
          <input
            className="form-control rounded-pill"
            type="text"
            placeholder="Search"
            value={searchPosts}
            onChange={(e) => setsearchPosts(e.target.value)}
          />
        </div>
        <div className="col-2">
          <button
            className="btn btn-orange rounded-pill"
            onClick={searchForPosts}
          >
            Search
          </button>
        </div>
      </div>
      <hr />

      <div className="row pb-5">
        {posts &&
          posts.map((post) => {
            return (
              <div className="col-4">
                <Link to={"/post/" + post._id} className="card">
                  <img
                    className="card-img-top"
                    src={
                      process.env.REACT_APP_SERVER_BASE + "upload/" + post.image
                    }
                    alt=""
                  />
                  <div className="card-body">
                    <h4 className="card-title">{post.title}</h4>
                    <p
                      className="card-text"
                      dangerouslySetInnerHTML={{
                        __html: draftToHtml(post.content),
                      }}
                    ></p>
                    <p className="card-text">
                      {/* <small className="text-muted">Last updated 3 mins ago</small> */}
                    </p>
                  </div>
                </Link>
              </div>
            );
          })}
      </div>
    </div>
  );
}
