import axios from "axios";
import { useState, useEffect } from "react";

import { Link } from "react-router-dom";

export default function LatestCategories() {
  const [searchCategories, setsearchCategories] = useState("");

  const [categories, setcategories] = useState([]);
  const [allCategory, setallCategory] = useState([]);

  useEffect(() => {
    getAllCategory();
  }, []);

  function searchForCategory(e) {
    e.preventDefault();

    const search = searchCategories;

    axios
      .post("/searchCategory", { search })
      .then((response) => {
        const res = response.data;
        setallCategory([]);
        setcategory(res);
        console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  function getAllCategory() {
    axios
      .post("/showCategory")
      .then((response) => {
        const res = response.data;
        const resCategories = res;
        setcategories(resCategories);
      })
      .catch((err) => {
        const error = err.response.data;
      });
    console.log(categories);
  }

  return (
    <div className="container py-5">
      <div className="row">
        <div className="col-10">
          {/* <label for="" className="form-label"></label> */}
          <input
            type="text"
            name="search"
            id="search"
            className="form-control rounded-pill"
            placeholder="Search"
            aria-describedby="helpId"
            onChange={(e) => setsearchCategory(e.target.value)}
          />
        </div>
        <button
          className="btn btn-orange rounded-pill col-2"
          onClick={searchForCategory}
        >
          Search
        </button>

        <div className="container py-4">
          <div className="row">
            {categories &&
              categories.map((value) => {
                return (
                  <Link to={"/category/" + value._id}>
                    <button
                      type="button"
                      className="btn btn-orange rounded-pill"
                    >
                      {value.categoryName}
                    </button>
                  </Link>
                );
              })}
            {allCategory &&
              allCategory.map((value) => {
                return (
                  <div className="col-4 mb-3">
                    <Link to={"/post/" + value._id} className="card">
                      {/* <img className="card-img-top" src={mobile} alt="" /> */}
                      <div className="card-body">
                        <h4 className="card-title">{value.title}</h4>
                        <p className="card-text">{value.content}</p>
                        <p className="card-text">
                          {/* <small className="text-muted">Last updated 3 mins ago</small> */}
                        </p>
                      </div>
                    </Link>
                  </div>
                );
              })}
          </div>
        </div>
      </div>
    </div>
  );
}
