import * as bootstrap from "bootstrap";
import axios from "axios";
import { toast } from "react-toastify";
import { useState, useEffect } from "react";
import draftToHtml from "draftjs-to-html";
import MyEditor from "./editor";
import Modal from "../../components/modal";
import SelectCategories from "../../components/selectCategories";
import SelectTags from "../../components/selectTags";
import ImageUpload from "../../components/imageUpload";

export default function EditPost() {
  const [postData, setPostData] = useState([]);
  const [content, setContent] = useState(null);
  const [editorContent, seteditorContent] = useState(null);
  const [editor, seteditor] = useState(false);
  const [formInput, setformInput] = useState({
    postImage: "",
    postTitle: "",
    postCategories: [],
    postTags: [],
    postContent: {},
  });

  useEffect(() => {
    showPosts();
  }, []);

  async function setModal(postID) {
    console.log(postID);
    // const postID = post_card.getAttribute("post_id");
    // const postTitle = post_card.querySelector(
    //   ".card-body > .card-title"
    // ).textContent;
    // const postContent = post_card.querySelector(
    //   ".card-body > .card-text"
    // ).innerHTML;

    const post = await axios
      .post("/getPost", { postID })
      .then((response) => {
        const res = response.data;
        console.log(res);
        setformInput({
          postID: res._id,
          postImage: res.image,
          postTitle: res.title,
          postCategories: res.categories,
          postTags: res.tags,
          postContent: res.content,
        });
      })
      .catch((err) => {
        const error = err.response.data;
        console.log(error);
      });

    // seteditorContent(postContent);
    // seteditor(true);
    // console.log(editorContent);

    // console.log(postID);
    // console.log(postTitle);

    // var myModalEl = document.getElementById("editPostModal");
    // var myModal = bootstrap.Modal.getOrCreateInstance(myModalEl);
    // myModal.show();

    // myModalEl.addEventListener("shown.bs.modal", () => {
    //   document.getElementById("postTitle").value = postTitle;
    //   setformInput({
    //     ...formInput,
    //     postID,
    //     postTitle,
    //   });
    //   // console.log(formInput);
    // });

    // myModalEl.addEventListener("hidden.bs.modal", () => {
    //   seteditor(false);
    //   console.log("closed");
    //   // console.log(formInput);
    // });
  }

  async function showPosts() {
    const posts = await axios
      .post("/showPost")
      .then((response) => {
        const res = response.data;
        let data = [];

        res.forEach((value) => {
          data.push({
            postID: value._id,
            postImage: value,
            postTitle: value.title,
            postCategories: value,
            postTags: value,
            postContent: draftToHtml(value.content),
          });

          // postData += `<div class="col-4 mb-2"><div class="card shadow-sm modal-click" post_id="${
          //   value._id
          // }"><div class="card-body"><h4 class="card-title">${
          //   value.title
          // }</h4><div class="card-text">${draftToHtml(
          //   value.content
          // )}</div></div></div></div>`;
        });
        return res;
        console.log(res);
        // console.log(postData);
        // document.getElementById("postContainer").innerHTML = postData;

        const post_cards = [].slice.call(
          document.getElementsByClassName("modal-click")
        );
        post_cards.forEach((post_card) => {
          post_card.addEventListener("click", () => {
            setModal(post_card);
          });
        });
        // return data;
        // console.log(res);
      })
      .catch((err) => {
        const error = err;
        console.log(error);
      });
    setPostData(posts);
    // console.log(postData);
  }

  function handleInput(e) {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    setformInput({
      ...formInput,
      [name]: value,
    });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    const config = {
      headers: {
        "content-type": "multipart/form-data",
      },
    };

    const formData = new FormData();

    Object.entries(formInput).forEach(([key, value]) => {
      if (key === "postImage") {
        formData.append(key, value);
      } else {
        formData.append(key, JSON.stringify(value));
      }
    });

    axios
      .post("/editPost", formData, config)
      .then((response) => {
        const res = response.data;
        console.log(res.success);
      })
      .catch((err) => {
        const error = err.response.data;
        console.log(error);
      });

    // console.log(formInput);
  }

  function handleDelete(e) {
    e.preventDefault();

    const postID = formInput.postID;

    // console.log();

    axios
      .post("/deletePost", { postID })
      .then((response) => {
        const res = response.data;
        const success = res.success;

        console.log(success);
        if (success.length > 0) {
          showPosts();
        }
      })
      .catch((err) => {
        const error = err.response.data;
      });

    var myModal = bootstrap.Modal.getOrCreateInstance(
      document.getElementById("editPostModal")
    );
    myModal.hide();
  }

  const getImage = (e) => {
    // console.log(e.target.files[0]);
    setformInput({
      postImage: e.target.files[0],
    });
    // console.log(formInput);
  };

  const getValue = (value) => {
    // check do you get dynamic value here
    // console.log("EditorValue: " + JSON.stringify(value));
    setContent(value);
  };

  const getCategories = (value) => {
    setformInput({ ...formInput, postCategories: value });
    console.log("Category: ", value);
  };
  const getTags = (value) => {
    setformInput({ ...formInput, postTags: value });
    console.log("Tags: ", value);
  };

  return (
    <div className="px-5">
      <div className="card shadow">
        <div className="card-body">
          <div id="postContainer" className="row">
            {console.log(postData)}
            {/* {postData.map((post) => (
              <div className="col-4 mb-2">
                <div
                  className="card shadow-sm"
                  data-bs-toggle="modal"
                  data-bs-target="#editPostModal"
                  onClick={() => {
                    setModal(post._id);
                    console.log(formInput);
                  }}
                >
                  <div className="card-body">
                    <h4 className="card-title">{post.title}</h4>
                    <div className="card-text">{draftToHtml(post.content)}</div>
                  </div>
                </div>
              </div>
            ))} */}
            {postData &&
              postData.map((post) => (
                <div className="col-lg-3 col-md-4 col-12">
                  <div
                    className="card mb-2"
                    data-bs-toggle="modal"
                    data-bs-target="#editPostModal"
                    onClick={() => {
                      setModal(post._id);
                      console.log(formInput);
                    }}
                  >
                    <img
                      className="card-img-top py-2 px-1"
                      src={
                        process.env.REACT_APP_SERVER_BASE +
                        "upload/" +
                        post.image
                      }
                      alt=""
                    />
                    <div className="card-body">
                      <h4 className="card-title">{post.title}</h4>
                      <p
                        className="card-text"
                        dangerouslySetInnerHTML={{
                          __html: draftToHtml(post.content),
                        }}
                      ></p>
                      <p className="card-text">
                        <small className="text-muted">
                          Last updated 3 mins ago
                        </small>
                      </p>
                    </div>
                  </div>
                </div>
              ))}
          </div>
          <Modal id={"editPostModal"} title={"Edit Post"}>
            <form>
              <ImageUpload
                label={"Upload Image"}
                getImage={getImage}
                defaultImage={
                  process.env.REACT_APP_SERVER_BASE + "No_Image_Available.jpg"
                }
              />
              <div className="mb-3">
                <label
                  htmlFor="postTitle"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Title
                </label>
                <div className="col-12">
                  <input
                    type="text"
                    className="form-control rounded-pill"
                    name="postTitle"
                    id="postTitle"
                    onChange={handleInput}
                    value={formInput.postTitle}
                  />
                </div>
              </div>

              <div className="mb-3">
                <label
                  htmlFor="post_cat"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Categories
                </label>
                <SelectCategories
                  getCategories={getCategories}
                  setCategories={formInput.postCategories}
                />
              </div>
              <div className="mb-3">
                <label
                  htmlFor="post_cat"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Tags
                </label>
                <SelectTags getTags={getTags} setTags={formInput.postTags} />
              </div>
              <div className="mb-3">
                <label
                  htmlFor="postDes"
                  className="col-12 ps-2 pb-1 text-black-50"
                >
                  Content
                </label>
                <div className="col-12">
                  {editor && (
                    <MyEditor
                      getValue={getValue}
                      editorContent={editorContent}
                    />
                  )}
                </div>
              </div>
              <div className="mb-5">
                <div className="col-sm-10">
                  <button
                    type="submit"
                    className="btn btn-orange"
                    onClick={handleSubmit}
                  >
                    Edit Post
                  </button>
                  <button
                    type="submit"
                    className="btn btn-danger ms-2"
                    // onClick={handleDelete}
                  >
                    Delete Post
                  </button>
                </div>
              </div>
            </form>
          </Modal>
        </div>
      </div>
    </div>
  );
}
