import axios from "axios";
import { useState } from "react";

import Cookies from "js-cookie";
import { toast } from "react-toastify";

export default function Profile() {
  const userInfo = JSON.parse(Cookies.get("user"));
  const [formInput, setformInput] = useState({
    image: {},
    name: "",
    email: "",
    oldPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  function handleFormChange(e) {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    setformInput({
      ...formInput,
      [name]: value,
    });
  }

  async function handleSubmit(e) {
    e.preventDefault();

    await axios
      .post("http://localhost:3001/loginAdmin", formInput)
      .then((response) => {
        const res = response.data;
        if (res.success) {
          toast.success(res.success);
        }
      })
      .catch((err) => {
        const errors = err.response.data;

        errors.forEach((error) => {
          toast.error(error);
        });
      });
  }

  setImage = (e) => {
    const imageFile = e.target.files[0];

    document.getElementById("profileImage").getAttribute("src").value =
      imageFile.filename;
    setformInput({
      ...formInput,
      image: imageFile,
    });
  };

  return (
    <div className="container">
      <div className="card p-4">
        <div className="card-body">
          <div className="row">
            <form action="#" method="post">
              <div className="row">
                <div className="col-4">
                  <label>
                    <img
                      src={
                        userInfo.image
                          ? userInfo.image
                          : "/images/No_Image_Available.jpg"
                      }
                      className="img-fluid rounded-circle mb-3"
                      htmlFor="image"
                      id="profileImage"
                      style={{ maxHeight: "196px" }}
                      alt="profile img"
                    />
                    <input type="file" className="d-none" name="image" />
                  </label>
                </div>
                <div className="col-8 pt-4">
                  <div className="mb-3">
                    <label
                      htmlFor="name"
                      className="col-12 ps-2 pb-1 text-black-50"
                    >
                      Name
                    </label>
                    <div className="col-12">
                      <input
                        type="text"
                        className="form-control rounded-pill"
                        name="name"
                        id="name"
                        value={userInfo.username}
                        onChange={handleFormChange}
                      />
                    </div>
                  </div>
                  <div className="mb-3">
                    <label
                      htmlFor="email"
                      className="col-12 ps-2 pb-1 text-black-50"
                    >
                      E-mail
                    </label>
                    <div className="col-12">
                      <input
                        type="email"
                        className="form-control rounded-pill"
                        name="email"
                        id="email"
                        value={userInfo.email}
                        onChange={handleFormChange}
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="row">
                <div className="mb-3 col-4">
                  <label
                    htmlFor="oldPassword"
                    className="col-12 ps-2 pb-1 text-black-50"
                  >
                    Old Password
                  </label>
                  <div className="col-12">
                    <input
                      type="password"
                      className="form-control rounded-pill"
                      name="oldPassword"
                      id="oldPassword"
                      onChange={handleFormChange}
                    />
                  </div>
                </div>
                <div className="mb-3 col-4">
                  <label
                    htmlFor="newPassword"
                    className="col-12 ps-2 pb-1 text-black-50"
                  >
                    New Password
                  </label>
                  <div className="col-12">
                    <input
                      type="password"
                      className="form-control rounded-pill"
                      name="newPassword"
                      id="newPassword"
                      onChange={handleFormChange}
                    />
                  </div>
                </div>
                <div className="mb-3 col-4">
                  <label
                    htmlFor="confirmPassword"
                    className="col-12 ps-2 pb-1 text-black-50"
                  >
                    Confirm Password
                  </label>
                  <div className="col-12">
                    <input
                      type="password"
                      className="form-control rounded-pill"
                      name="confirmPassword"
                      id="confirmPassword"
                      onChange={handleFormChange}
                    />
                  </div>
                </div>
              </div>
              <div className="col-12">
                <button
                  type="submit"
                  className="btn btn-orange w-100"
                  onClick={handleSubmit}
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
