import axios from "axios";
import draftToHtml from "draftjs-to-html";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

export default function Category(props) {
  const [posts, setPosts] = useState(null);
  const params = useParams();

  async function getCategoryPosts(category) {
    const getPosts = await axios
      .post("/getCategoryPosts", { category })
      .then((response) => {
        const res = response.data;
        console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
        console.log(error);
      });
    setPosts(getPosts);
  }

  useEffect(() => {
    getCategoryPosts(params.category);
  }, []);

  return (
    <div className="container">
      <div className="text-center py-5">
        <h2 className="display-5">Category</h2>
      </div>

      <div className="row pb-5">
        {posts &&
          posts.map((post) => (
            <div className="col-4">
              <Link to={"/post/" + post._id} className="card">
                <img className="card-img-top" src="/images/tech-1.jpg" alt="" />
                <div className="card-body">
                  <h4 className="card-title">{post.title}</h4>
                  <p className="card-text">{draftToHtml(post.content)}</p>
                  <p className="card-text">
                    <small className="text-muted">
                      Last updated 3 mins ago
                    </small>
                  </p>
                </div>
              </Link>
            </div>
          ))}

        <div className="col-4">
          <div className="card">
            <img className="card-img-top" src="/images/tech-1.jpg" alt="" />
            <div className="card-body">
              <h4 className="card-title">Card title</h4>
              <p className="card-text">
                This is a wider card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </p>
              <p className="card-text">
                <small className="text-muted">Last updated 3 mins ago</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-4">
          <div className="card">
            <img className="card-img-top" src="/images/tech-1.jpg" alt="" />
            <div className="card-body">
              <h4 className="card-title">Card title</h4>
              <p className="card-text">
                This is a wider card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </p>
              <p className="card-text">
                <small className="text-muted">Last updated 3 mins ago</small>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
