import tech from "./assets/images/tech-1.jpg";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import PostCard from "./components/postCard";

export default function Home() {
  // const [search, setsearch] = useState("");
  const [categories, setcategories] = useState([]);
  const [posts, setposts] = useState([]);

  useEffect(() => {
    // Show categories on page load
    getCategories();

    // Show posts on page load
    getPosts();
  }, []);

  const getCategories = () => {
    axios
      .post("/showCategory")
      .then((response) => {
        const res = response.data;
        const resCategories = res;
        setcategories(resCategories);
        // categories = resCategories;
        // console.log(resCategories);
      })
      .catch((err) => {
        const error = err.response.data;
      });
    console.log(categories);
  };

  const getPosts = () => {
    axios
      .post("/showPost")
      .then((response) => {
        const res = response.data;
        const resPosts = res;
        setposts(resPosts);
        // console.log(resPosts);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  };

  return (
    <div>
      <div className="header-content">
        <img
          src={tech}
          className="img-fluid"
          alt=""
          width="100%"
          height="100%"
        />
        <div className="overlay row h-100 w-100">
          <div className="w-100 p-5 my-auto text-center text-white">
            <h1 className="display-4 fw-bold">Creative Blog</h1>
            <p className="lead">
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Obcaecati
              earum consequuntur, voluptatibus officia beatae, corrupti
              voluptate cumque quisquam asperiores voluptas ex! Quisquam,
              veritatis ipsa? Dicta eligendi id hic aperiam similique.
            </p>
          </div>
        </div>
      </div>

      <div className="container py-5 text-center">
        <h2 className="display-4 pb-4">Categories</h2>
        {categories &&
          categories.map((value) => {
            return (
              <Link to={"/category/" + value.categoryName}>
                <button type="button" className="btn btn-orange rounded-pill">
                  {value.categoryName}
                </button>
              </Link>
            );
          })}
      </div>

      {posts && <PostCard posts={posts} />}
    </div>
  );
}
