import { useState } from "react";

export default function ImageUpload({ label, getImage }) {
  const [imageContent, setimageContent] = useState();

  function setImage(e) {
    setimageContent(URL.createObjectURL(e.target.files[0]));
  }

  return (
    <div className="mb-3">
      <label htmlFor="post_image" className="form-label text-black-50">
        {label}
      </label>
      <input
        className="form-control hidden"
        type="file"
        id="post_image"
        onChange={(e) => {
          setImage(e);
          getImage(e);
        }}
      />
      {imageContent && (
        <img src={imageContent} className="img-fluid" htmlFor="post_image" />
      )}
    </div>
  );
}
