import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function Search() {
  const params = useParams();

  const [searchPosts, setsearchPosts] = useState(params.search);
  useEffect(() => {
    searchForPosts(e);
  }, []);

  function searchForPosts(e) {
    if (e) {
      e.preventDefault();
    }

    const search = searchPosts;

    axios
      .post("/searchPost", { search })
      .then((response) => {
        const res = response.data;
        setallPosts([]);
        setposts(res);
        console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
      });
  }

  return (
    <div className="container">
      <div className="text-center py-5">
        <h2 className="display-5">Search</h2>
      </div>

      <div className="search-container">
        <form className="d-flex">
          <input
            className="form-control rounded-pill"
            type="text"
            placeholder="Search"
            value={params}
          />
          <button className="btn btn-outline-orange w-25" type="submit">
            Search
          </button>
        </form>
        <hr />
      </div>

      <div className="row pb-5">
        <div className="col-4">
          <div className="card">
            <img className="card-img-top" src="/images/tech-1.jpg" alt="" />
            <div className="card-body">
              <h4 className="card-title">Card title</h4>
              <p className="card-text">
                This is a wider card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </p>
              <p className="card-text">
                <small className="text-muted">Last updated 3 mins ago</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-4">
          <div className="card">
            <img className="card-img-top" src="/images/tech-1.jpg" alt="" />
            <div className="card-body">
              <h4 className="card-title">Card title</h4>
              <p className="card-text">
                This is a wider card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </p>
              <p className="card-text">
                <small className="text-muted">Last updated 3 mins ago</small>
              </p>
            </div>
          </div>
        </div>
        <div className="col-4">
          <div className="card">
            <img className="card-img-top" src="/images/tech-1.jpg" alt="" />
            <div className="card-body">
              <h4 className="card-title">Card title</h4>
              <p className="card-text">
                This is a wider card with supporting text below as a natural
                lead-in to additional content. This content is a little bit
                longer.
              </p>
              <p className="card-text">
                <small className="text-muted">Last updated 3 mins ago</small>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
