import React, { useState, useEffect, useRef, useMemo } from "react";
import JoditEditor from "jodit-react";

const Editor = ({ getValue, content }) => {
  const editor = useRef(null);

  // useEffect(() => {}, [content]);

  const config = {
    buttons: [
      "bold",
      "italic",
      "underline",
      "strikethrough",
      "eraser",
      "ul",
      "ol",
      "font",
      "fontsize",
      "paragraph",
      "classSpan",
      "lineHeight",
      "image",
      "video",
      "file",
      "spellcheck",
      "copyformat",
      "cut",
      "copy",
    ],
    readonly: false, // all options from https://xdsoft.net/jodit/doc/
  };

  const getValues = (value) => {
    // check do you get dynamic value here
    console.log(value);
    // setContent(value);
  };

  return (
    <JoditEditor
      value={content}
      onBlur={(newContent) => getValues(newContent)} // preferred to use only this option to update the content for performance reasons
      onChange={(newContent) => {
        getValues(newContent);
      }}
    />
  );
};

export default Editor;
