const Editor = ({ getValue, content }) => {
  // const editor = useRef(null);
  const editor2 = useRef(null);

  useEffect(() => {
    const textarea = Jodit.make("#editor");
  }, [content]);

  const config = {
    buttons: [
      "bold",
      "italic",
      "underline",
      "strikethrough",
      "eraser",
      "ul",
      "ol",
      "font",
      "fontsize",
      "paragraph",
      "classSpan",
      "lineHeight",
      "image",
      "video",
      "file",
      "spellcheck",
      "copyformat",
      "cut",
      "copy",
    ],
    readonly: false, // all options from https://xdsoft.net/jodit/doc/
  };

  const getValues = (value) => {
