import axios from "axios";
import { toast } from "react-toastify";
import { useState } from "react";
import MyEditor from "./editor";
import ImageUpload from "../../components/imageUpload";
import SelectCategories from "../../components/selectCategories";
import SelectTags from "../../components/selectTags";

export default function CreatePost() {
  const [forminput, setforminput] = useState({});
  const [content, setContent] = useState(null);

  function handleInput(event) {
    const target = event.target;
    const name = target.name;
    const value = target.value;

    setforminput({
      ...forminput,
      [name]: value,
    });

    // console.log(forminput);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    // console.log(forminput.postImage);

    const config = {
      headers: {
        "content-type": "multipart/form-data",
      },
    };

    const formData = new FormData();

    Object.keys(forminput).forEach((i) => {
      console.log(i + ", " + forminput[i]);
      formData.append(i, forminput[i]);
    });

    // for (var pair of formData.entries()) {
    //   console.log(pair[0] + ", " + pair[1]);
    // }

    await axios
      // .post(
      //   "/createPost",
      //   { ...forminput, formData, postContent: content },
      //   config
      // )
      .post("/createPost", formData, config)
      .then((response) => {
        const res = response.data;
        const success = res.success;

        console.log(success);

        if (success.length > 0) {
          toast.success(success, {
            position: toast.POSITION.TOP_RIGHT,
          });
        }
      })
      .catch((err) => {
        const error = err.response.data;
        console.log(error);
        if (error.length > 0) {
          toast.error(error, {
            position: toast.POSITION.TOP_RIGHT,
          });
        }
      });
  }

  const getImage = (e) => {
    // console.log(e.target.files[0]);
    setforminput({
      postImage: e.target.files[0],
    });
  };

  const getValue = (value) => {
    // check do you get dynamic value here
    // console.log("EditorValue: " + JSON.stringify(value));
    setContent(value);
    setforminput({ ...forminput, postContent: value });
  };
  const getCategories = (value) => {
    setforminput({ ...forminput, postCategories: value });
  };
  const getTags = (value) => {
    setforminput({ ...forminput, postTags: value });
  };

  return (
    <div className="px-5">
      <div className="card shadow">
        <div className="card-body">
          <div className="h2 p-1">Create Post</div>
          <form>
            <ImageUpload label={"Upload Image"} getImage={getImage} />
            <div className="mb-3">
              <label
                htmlFor="post_title"
                className="col-12 ps-2 pb-1 text-black-50"
              >
                Title
              </label>
              <div className="col-12">
                <input
                  type="text"
                  className="form-control rounded-pill"
                  name="postTitle"
                  id="postTitle"
                  onChange={handleInput}
                />
              </div>
            </div>
            <div className="mb-3">
              <label
                htmlFor="post_cat"
                className="col-12 ps-2 pb-1 text-black-50"
              >
                Categories
              </label>
              <SelectCategories getCategories={getCategories} />
            </div>
            <div className="mb-3">
              <label
                htmlFor="post_cat"
                className="col-12 ps-2 pb-1 text-black-50"
              >
                Tags
              </label>
              <SelectTags getTags={getTags} />
            </div>
            <div className="mb-3">
              <label
                htmlFor="post_des"
                className="col-12 ps-2 pb-1 text-black-50"
              >
                Content
              </label>
              <div className="col-12">
                <MyEditor getValue={getValue} editorContent={""} />
              </div>
            </div>
            <div className="mb-5">
              <div className="col-sm-10">
                <button
                  type="submit"
                  className="btn btn-orange"
                  onClick={handleSubmit}
                >
                  Submit
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
