import axios from "axios";
import { useState } from "react";

export default function Profile() {
  const userInfo = JSON.parse(Cookies.get("user"));
  const [formInput, setformInput] = useState({});

  function handleFormChange(e) {
    const Target = e.target;
    const Name = Target.name;
    const Value = Target.value;

    setformInput({
      ...formInput,
      [Name]: Value,
    });
  }

  function handleSubmit(e) {
    e.preventDefault();

    document.getElementById("form_message").style.display = "none";
    axios
      .post("http://localhost:3001/loginAdmin", formInput)
      .then((response) => {
        console.log(response.data.length);
        document
          .getElementById("form_message")
          .classList.remove("alert-danger");
        document.getElementById("form_message").classList.add("alert-success");
        document.getElementById("form_message").innerHTML =
          response.data.success;
        document.getElementById("form_message").style.display = "block";
      })
      .catch((err) => {
        // if (err.response && err.response.status === 400) {
        //   console.clear();
        // }
        let errors = "";
        Object.values([err.response.data]).map(function ({ email, password }) {
          // console.log(email);
          if ((email || password) !== null) {
            errors += '<p class="mb-1">' + email + "</p>";
            errors += '<p class="mb-1">' + password + "</p>";
          }
          // console.log(email);
          return errors;
        });
        // console.log(errors.length);

        if (errors.length > 0) {
          document
            .getElementById("form_message")
            .classList.remove("alert-success");
          document.getElementById("form_message").classList.add("alert-danger");
          document.getElementById("form_message").innerHTML = errors;
          document.getElementById("form_message").style.display = "block";
        }
      });
  }

  return (
    <div className="container">
      <div className="card p-4">
        <div className="card-body">
          <div className="row">
            <form action="#" method="post">
              <div className="row">
                <div className="col-4">
                  {/* <img
                    src=""
                    className="img-fluid rounded-circle mb-3"
                    style={{max-height: "196px"}}
                    alt="profile img"
                  /> */}
                </div>
                <div className="col-8 pt-4">
                  <div className="mb-3">
                    <label
                      htmlFor="name"
                      className="col-12 ps-2 pb-1 text-black-50"
                    >
                      Name
                    </label>
                    <div className="col-12">
                      <input
                        type="text"
                        className="form-control rounded-pill"
                        name="name"
                        id="name"
                        value={userInfo.username}
                        onChange={handleFormChange}
                      />
                    </div>
                  </div>
                  <div className="mb-3">
                    <label
                      htmlFor="email"
                      className="col-12 ps-2 pb-1 text-black-50"
                    >
                      E-mail
                    </label>
                    <div className="col-12">
                      <input
                        type="email"
                        className="form-control rounded-pill"
                        name="email"
                        id="email"
                        value={userInfo.email}
                        onChange={handleFormChange}
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="row">
                <div className="mb-3 col-4">
                  <label
                    htmlFor="oldpass"
                    className="col-12 ps-2 pb-1 text-black-50"
                  >
                    Old Password
                  </label>
                  <div className="col-12">
                    <input
                      type="password"
                      className="form-control rounded-pill"
                      name="oldpass"
                      id="oldpass"
                      onChange={handleFormChange}
                    />
                  </div>
                </div>
                <div className="mb-3 col-4">
                  <label
                    htmlFor="newpass"
                    className="col-12 ps-2 pb-1 text-black-50"
                  >
                    New Password
                  </label>
                  <div className="col-12">
                    <input
                      type="password"
                      className="form-control rounded-pill"
                      name="newpass"
                      id="newpass"
                      onChange={handleFormChange}
                    />
                  </div>
                </div>
                <div className="mb-3 col-4">
                  <label
                    htmlFor="confirmpass"
                    className="col-12 ps-2 pb-1 text-black-50"
                  >
                    Confirm Password
                  </label>
                  <div className="col-12">
                    <input
                      type="password"
                      className="form-control rounded-pill"
                      name="confirmpass"
                      id="confirmpass"
                      onChange={handleFormChange}
                    />
                  </div>
                </div>
              </div>
              <div className="col-12">
                <button
                  type="submit"
                  className="btn btn-orange w-100"
                  onClick={handleSubmit}
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
