import { useState, useEffect, useRef } from "react";
import { EditorState, convertFromRaw, convertToRaw } from "draft-js";
import { Editor } from "react-draft-wysiwyg";

import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

export default function MyEditor({ getValue }) {
  const [editorState, setEditorState] = useState(() =>
    EditorState.createEmpty()
  );
  // const [editorState, setEditorState] = useState(() => convertFromRaw(content));
  const [editorData, seteditorData] = useState(null);
  // const editorState = useRef(EditorState.createEmpty());

  useEffect(() => {
    setEditorState(EditorState.createWithContent(convertFromRaw(content)));
    // setEditorState(convertFromRaw(content));
  }, []);

  const content = {
    blocks: [
      {
        key: "637gr",
        text: "Initialized from content.",
        type: "unstyled",
        depth: 0,
        inlineStyleRanges: [
          {
            offset: 0,
            length: 11,
            style: "BOLD",
          },
        ],
        entityRanges: [],
        data: {},
      },
    ],
    entityMap: {},
  };

  const updateTextDescription = async (state) => {
    await setEditorState(state);

    const data = convertToRaw(editorState.getCurrentContent());
    // console.log(data);
    getValue(data);
    // await seteditorData(data);
  };

  return (
    <div>
      <Editor
        editorState={editorState}
        toolbarClassName="toolbarClassName"
        wrapperClassName="wrapperClassName"
        editorClassName="editorClassName"
        onEditorStateChange={updateTextDescription}
      />

      <div>{editorData}</div>
    </div>
  );
}

