import { useState } from "react";

export default function ImageUpload({ label, getImage }) {
  const [imageContent, setimageContent] = useState();

  function setImage(e) {
    setimageContent(URL.createObjectURL(e.target.files[0]));
  }

  return (
    <div className="mb-3">
      <label className="form-label text-black-50">{label}</label>
      <br />
      <label htmlFor="postImage" className="form-label text-black-50">
        {imageContent ? (
          <img src={imageContent} className="img-fluid" htmlFor="postImage" />
        ) : (
          <img
            src={process.env.REACT_APP_SERVER_BASE + "No_Image_Available.jpg"}
            className="img-fluid w-50"
            htmlFor="postImage"
          />
        )}
      </label>
      <input
        // {imageContent ? : }
        className="form-control d-none"
        type="file"
        accept="image/*"
        id="postImage"
        name="postImage"
        onChange={(e) => {
          setImage(e);
          getImage(e);
        }}
      />
    </div>
  );
}
