import { useEffect, useState } from "react";
import axios from "axios";
import Cookies from "js-cookie";

export default function Dashboard() {
  const [countCategories, setCountCategories] = useState(0);
  const [countTags, setCountTags] = useState(0);
  const [countPosts, setCountPosts] = useState(0);

  const user = JSON.parse(Cookies.get("user"));
  const token = user.token;
  const config = {
    headers: {
      authorization: `Bearer ${token}`,
    },
  };

  async function getCategories() {
    await axios
      .post(process.env.REACT_APP_SERVER_BASE + "showCategory", {}, config)
      .then(async (response) => {
        let count = 1;
        const res = await response.data;
        // res.forEach(() => {
        //   console.log(index);
        // });
        while (res.length < count) {
          console.log(count++);
        }
      })
      .catch((err) => {
        const error = err.response.data;
        console.log(error);
      });
  }

  async function getTags() {
    await axios
      .post(process.env.REACT_APP_SERVER_BASE + "showTag", {}, config)
      .then((response) => {
        const res = response.data;
        // console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
        console.log(error);
      });
  }

  async function getPosts() {
    await axios
      .post(process.env.REACT_APP_SERVER_BASE + "showPost", {}, config)
      .then((response) => {
        const res = response.data;
        // console.log(res);
      })
      .catch((err) => {
        const error = err.response.data;
        console.log(error);
      });
  }

  useEffect(() => {
    getCategories();
    getTags();
    getPosts();
  }, []);

  return (
    <div className="container">
      <div className="row">
        <div className="col-6">
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Categories</h4>
              <p className="card-text">{countCategories}</p>
            </div>
          </div>
        </div>

        <div className="col-6">
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Tags</h4>
              <p className="card-text">{countTags}</p>
            </div>
          </div>
        </div>

        <div className="col-12">
          <div className="card mb-4">
            <div className="card-body">
              <h4 className="card-title">Posts</h4>
              <p className="card-text">{countPosts}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
